export function addTask(task) {
  return {
    type: "ADD_TASK",
    payload: task
  };
}

export function deleteTask(taskId) {
  return {
    type: "DELETE_TASK",
    id: taskId
  };
}

export function markTask(taskId) {
  return {
    type: "MARK_TASK",
    markid: taskId
  };
}
