import React, { Component } from "react";
import Header from "./components/header/";
import TaskBar from "./components/taskbar/";
import TaskList from "./components/tasklist/";
import store from "./reducers";
import Filter from "./components/filter/";
import "./style.css";
import { connect } from "react-redux";

class App extends Component {
  state = {
    d: ""
  };

  componentDidMount() {
    store.subscribe(() => {
      console.log("Logged");
      this.setState({ d: "" });
    });
  }

  render() {
    let todo = this.props.todo;
    let filter = this.props.filter;
    let filteredState = { todo: [], filter: "" };
    //store.getState().filter
    if (filter === "ACTIVE_FILTER") {
      filteredState.todo = todo.filter(state => !state.isCompleted);
      filteredState.filter = filter;
      console.log(filteredState);
    }

    if (filter === "DONE_FILTER") {
      filteredState.todo = todo.filter(state => state.isCompleted);
      filteredState.filter = filter;
      console.log(filteredState);
    }

    if (filter === "ALL_FILTER" || filter === "") {
      filteredState.todo = todo;
      filteredState.filter = filter;
      console.log(filteredState);
    }

    return (
      <div id="one">
        <Header />
        <TaskBar />
        <TaskList todo={filteredState} />
        <Filter />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    todoItems: state.todo,
    filter: state.filter
  };
};

const tasklist = connect(
  mapStateToProps,
  null
)(App);
export default tasklist;

//export default connect()(App);

