import { createStore } from "redux";

const taskReducer = (state = { todo: [], filter: "" }, action) => {
  switch (action.type) {
    case "ADD_TASK":
      state = [...state, { caption: action.payload, isCompleted: false }];
      break;
    case "DELETE_TASK":
      state = state.slice();
      console.log(action.id);
      state.splice(action.id, 1);
      break;
    case "MARK_TASK":
      state[action.id].isCompleted = !state[action.id].isCompleted;
      break;
    default:
      break;
  }

  switch (action.filter) {
    case "ALL_FILTER":
      state.filter = "ALL_FILTER";
      break;
    case "ACTIVE_FILTER":
      break;
    default:
      break;
  }

  return state;
};
let store = createStore(taskReducer);

export default store;
