      <tr>
        <td>
          (state[id].isCompleted) ? state[id].caption.
          {this.props.task}
        </td>
        <td>
          <button
            onClick={() => {
              store.dispatch(deleteTask(this.props.id));
            }}
          >
            Delete
          </button>
        </td>
        <td>
          <button
            onClick={() => {
              store.dispatch(markTask(this.props.id));
            }}
          >
            Mark as done
          </button>
        </td>
      </tr>
    );
  }
}

export default Task;

